package main

import (
    "fmt"
    "net/http"
    "sync"
    "time"

    "github.com/aws/aws-lambda-go/lambdaurl"
    "github.com/gin-gonic/gin"
)

// album represents data about a record album.
type album struct {
    ID     string  `json:"id"`
    Title  string  `json:"title"`
    Artist string  `json:"artist"`
    Price  float64 `json:"price"`
}

// albumCount represents the count of albums by ID.
type albumCount struct {
    ID    string `json:"id"`
    Count int    `json:"count"`
}

// albums slice to seed record album data.
var albums = []album{
    {ID: "1", Title: "Blue Train", Artist: "John Coltrane", Price: 56.99},
    {ID: "2", Title: "Jeru", Artist: "Gerry Mulligan", Price: 17.99},
    {ID: "3", Title: "Sarah Vaughan and Clifford Brown", Artist: "Sarah Vaughan", Price: 39.99},
}

// albumCounts slice to track album counts.
var albumCounts []albumCount

func main() {
    // Initialize Gin router.
    router := gin.Default()

    // Register all routes.
    router.GET("/albums", getAlbums)
    router.GET("/albums/:id", getAlbumByID)
    router.POST("/albums", postAlbums)
    router.GET("/album-count/:id", getAlbumCount)
    router.POST("/album-count/:id", postAlbumCount)

    // Start Lambda with lambdaurl (for Function URLs).
    lambdaurl.Start(router)
}

// getAlbums responds with the list of all albums as JSON.
func getAlbums(c *gin.Context) {
    c.IndentedJSON(http.StatusOK, albums)
}

// postAlbums adds an album from JSON received in the request body.
func postAlbums(c *gin.Context) {
    var newAlbum album

    // Call BindJSON to bind the received JSON to
    // newAlbum.
    if err := c.BindJSON(&newAlbum); err != nil {
        return
    }

    // Add the new album to the slice.
    albums = append(albums, newAlbum)
    c.IndentedJSON(http.StatusCreated, newAlbum)
}

// getAlbumByID locates the album whose ID value matches the id
// parameter sent by the client, then returns that album as a response.
func getAlbumByID(c *gin.Context) {
    id := c.Param("id")

    // Loop through the list of albums, looking for
    // an album whose ID value matches the parameter.
    for _, a := range albums {
        if a.ID == id {
            c.IndentedJSON(http.StatusOK, a)
            return
        }
    }
    c.IndentedJSON(http.StatusNotFound, gin.H{"message": "album not found"})
}

// getAlbumCount retrieves the count for a given album ID.
func getAlbumCount(c *gin.Context) {
    id := c.Param("id")

    // Loop through the albumCounts slice to find matching ID.
    for _, ac := range albumCounts {
        if ac.ID == id {
            c.IndentedJSON(http.StatusOK, ac)
            return
        }
    }

    // Return 0 if ID not found.
    c.IndentedJSON(http.StatusOK, albumCount{ID: id, Count: 0})
}

// postAlbumCount adds 10000 to the counter in parallel.
func postAlbumCount(c *gin.Context) {
    id := c.Param("id")

    // Find the albumCount entry or create it.
    index := -1
    for i, ac := range albumCounts {
        if ac.ID == id {
            index = i
            break
        }
    }

    // Create new entry if not exists.
    if index == -1 {
        albumCounts = append(albumCounts, albumCount{ID: id, Count: 0})
        index = len(albumCounts) - 1
    }

    // Spawn 10000 goroutines to increment counter.
    var wg sync.WaitGroup
    for i := 0; i < 10000; i++ {
        wg.Add(1)
        go func() {
            defer wg.Done()
            current := albumCounts[index].Count
            albumCounts[index].Count = current + 1
        }()
    }

    // Wait for all goroutines to complete.
    wg.Wait()

    // Get final count.
    finalCount := albumCounts[index].Count

    // Log to stdout (captured by CloudWatch in Lambda).
    fmt.Printf("[%s] Album ID: %s, Final Count: %d\n",
        time.Now().Format(time.RFC3339), id, finalCount)

    c.IndentedJSON(http.StatusOK, albumCount{ID: id, Count: finalCount})
}

.PHONY: build deploy create update clean check-exists setup-role setup-function-url logs test delete info help

# Configuration
FUNCTION_NAME := album-counter
ROLE_NAME := album-counter-lambda-role
REGION := us-west-2
MEMORY := 3008
TIMEOUT := 30

# Build the Lambda binary and create zip
build:
	@echo "Building Lambda function..."
	GOOS=linux GOARCH=amd64 go build -o bootstrap main.go
	zip -q function.zip bootstrap
	@echo "✓ Build complete: function.zip"

# Check if Lambda function exists
check-exists:
	@aws lambda get-function --function-name $(FUNCTION_NAME) --region $(REGION) > /dev/null 2>&1

# Check if IAM role exists, create if not
setup-role:
	@echo "Checking IAM role..."
	@if ! aws iam get-role --role-name $(ROLE_NAME) > /dev/null 2>&1; then \
		echo "Creating IAM role $(ROLE_NAME)..."; \
		echo '{"Version":"2012-10-17","Statement":[{"Effect":"Allow","Principal":{"Service":"lambda.amazonaws.com"},"Action":"sts:AssumeRole"}]}' > /tmp/trust-policy.json; \
		aws iam create-role \
			--role-name $(ROLE_NAME) \
			--assume-role-policy-document file:///tmp/trust-policy.json \
			--description "Execution role for $(FUNCTION_NAME)" > /dev/null; \
		aws iam attach-role-policy \
			--role-name $(ROLE_NAME) \
			--policy-arn arn:aws:iam::aws:policy/service-role/AWSLambdaBasicExecutionRole; \
		rm /tmp/trust-policy.json; \
		echo "✓ IAM role created"; \
		echo "Waiting 10 seconds for IAM role to propagate..."; \
		sleep 10; \
	else \
		echo "✓ IAM role already exists"; \
	fi

# Create new Lambda function
create: build setup-role
	@echo "Creating Lambda function $(FUNCTION_NAME)..."
	@ROLE_ARN=$$(aws iam get-role --role-name $(ROLE_NAME) --query 'Role.Arn' --output text); \
	aws lambda create-function \
		--function-name $(FUNCTION_NAME) \
		--runtime provided.al2023 \
		--role $$ROLE_ARN \
		--handler bootstrap \
		--zip-file fileb://function.zip \
		--timeout $(TIMEOUT) \
		--memory-size $(MEMORY) \
		--region $(REGION) \
		--description "Album counter API with intentional race conditions" > /dev/null
	@echo "✓ Lambda function created"
	@$(MAKE) setup-function-url

# Update existing Lambda function
update: build
	@echo "Updating Lambda function $(FUNCTION_NAME)..."
	@aws lambda update-function-code \
		--function-name $(FUNCTION_NAME) \
		--zip-file fileb://function.zip \
		--region $(REGION) > /dev/null
	@echo "✓ Lambda function updated"

# Setup Function URL (idempotent)
setup-function-url:
	@echo "Setting up Function URL..."
	@if ! aws lambda get-function-url-config --function-name $(FUNCTION_NAME) --region $(REGION) > /dev/null 2>&1; then \
		aws lambda create-function-url-config \
			--function-name $(FUNCTION_NAME) \
			--auth-type NONE \
			--invoke-mode RESPONSE_STREAM \
			--cors AllowOrigins="*",AllowMethods="GET,POST",AllowHeaders="*",MaxAge=86400 \
			--region $(REGION) > /dev/null; \
		echo "✓ Function URL created"; \
	else \
		echo "✓ Function URL already exists"; \
	fi
	@echo "Setting up public access permission..."
	@aws lambda add-permission \
		--function-name $(FUNCTION_NAME) \
		--statement-id FunctionURLAllowPublicAccess \
		--action lambda:InvokeFunctionUrl \
		--principal "*" \
		--function-url-auth-type NONE \
		--region $(REGION) > /dev/null 2>&1 || echo "✓ Permission already exists"
	@echo "✓ Function URL configured"
	@echo ""
	@echo "Function URL:"
	@aws lambda get-function-url-config --function-name $(FUNCTION_NAME) --region $(REGION) --query 'FunctionUrl' --output text

# Smart deploy: create if not exists, update if exists
deploy: build
	@if $(MAKE) -s check-exists 2>/dev/null; then \
		echo "Lambda function exists, updating..."; \
		$(MAKE) update; \
	else \
		echo "Lambda function does not exist, creating..."; \
		$(MAKE) create; \
	fi
	@echo ""
	@echo "✓ Deployment complete!"
	@echo ""
	@echo "Function URL:"
	@aws lambda get-function-url-config --function-name $(FUNCTION_NAME) --region $(REGION) --query 'FunctionUrl' --output text 2>/dev/null || echo "Not configured"

# View recent CloudWatch logs
logs:
	@echo "Fetching recent logs from /aws/lambda/$(FUNCTION_NAME)..."
	@aws logs tail /aws/lambda/$(FUNCTION_NAME) --region $(REGION) --since 10m --format short --follow

# Test the deployed function
test:
	@FUNCTION_URL=$$(aws lambda get-function-url-config --function-name $(FUNCTION_NAME) --region $(REGION) --query 'FunctionUrl' --output text 2>/dev/null | sed 's:/*$$::'); \
	if [ -z "$$FUNCTION_URL" ]; then \
		echo "Error: Function URL not found. Run 'make deploy' first."; \
		exit 1; \
	fi; \
	echo "Testing GET /albums..."; \
	curl -s "$$FUNCTION_URL/albums" | head -10; \
	echo ""; \
	echo ""; \
	echo "Testing POST /album-count/1..."; \
	curl -s -X POST "$$FUNCTION_URL/album-count/1"

# Delete Lambda function and related resources
delete:
	@echo "Deleting Lambda function..."
	@aws lambda delete-function-url-config --function-name $(FUNCTION_NAME) --region $(REGION) 2>/dev/null || true
	@aws lambda delete-function --function-name $(FUNCTION_NAME) --region $(REGION) 2>/dev/null || true
	@aws iam detach-role-policy --role-name $(ROLE_NAME) --policy-arn arn:aws:iam::aws:policy/service-role/AWSLambdaBasicExecutionRole 2>/dev/null || true
	@aws iam delete-role --role-name $(ROLE_NAME) 2>/dev/null || true
	@echo "✓ Cleanup complete"

# Clean local build artifacts
clean:
	@echo "Cleaning build artifacts..."
	@rm -f bootstrap function.zip
	@echo "✓ Clean complete"

# Show function info
info:
	@echo "Function: $(FUNCTION_NAME)"
	@echo "Region: $(REGION)"
	@echo "Memory: $(MEMORY) MB"
	@echo "Timeout: $(TIMEOUT) seconds"
	@echo ""
	@if $(MAKE) -s check-exists 2>/dev/null; then \
		echo "Status: EXISTS"; \
		echo ""; \
		aws lambda get-function --function-name $(FUNCTION_NAME) --region $(REGION) --query 'Configuration.[State,LastModified,CodeSize]' --output text; \
		echo ""; \
		FUNCTION_URL=$$(aws lambda get-function-url-config --function-name $(FUNCTION_NAME) --region $(REGION) --query 'FunctionUrl' --output text 2>/dev/null); \
		if [ -n "$$FUNCTION_URL" ]; then \
			echo "Function URL: $$FUNCTION_URL"; \
		fi; \
	else \
		echo "Status: NOT FOUND"; \
	fi

# Help
help:
	@echo "Available targets:"
	@echo "  make build    - Build Lambda binary and create zip"
	@echo "  make deploy   - Smart deploy (creates or updates automatically)"
	@echo "  make create   - Create new Lambda function"
	@echo "  make update   - Update existing Lambda function"
	@echo "  make test     - Test deployed endpoints"
	@echo "  make logs     - Tail CloudWatch logs"
	@echo "  make info     - Show function information"
	@echo "  make delete   - Delete Lambda and IAM resources"
	@echo "  make clean    - Remove local build artifacts"
	@echo ""
	@echo "Quick start: make deploy"

